import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch  } from 'react-redux';
import { getGames } from '../../store/actions';
import { Spinner, Button } from 'react-bootstrap';

const HomeGames = () => {
    const homeGame = useSelector(state => state.games);
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getGames({},1,"desc",6))
    },[dispatch])

    const loadMoreGames = () => {
        const page = homeGame + 1
        dispatch(getGames(page,"dec",6))
    }
    return (
        <>
            <Button variant="outline-dark" onClick={() => loadMoreGames()}>
                Load more games
            </Button>
        </>
    )    
}

export default HomeGames;
