import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch  } from 'react-redux';
import { getGames } from '../../store/actions';
import { Spinner, Button, Card } from 'react-bootstrap';
// import Masonry from 'react-masonry-css';
import Moment from 'react-moment';
import { LinkContainer } from 'react-router-bootstrap';


const HomeGames = () => {
    const homeGames = useSelector(state => state.games);
    const dispatch = useDispatch();
    const [loading,setLoading] = useState(false);


    useEffect(() => {
        dispatch(getGames({},1,"desc",6))
    },[dispatch])

    const loadMoreGames = () => {
        const page = homeGames.page + 1;
        setLoading(true);
        dispatch(getGames(homeGames,page,"desc",6)).then(() => {
            setLoading(false);
        })
    }
    
    return (
        <>
            <Card 
                style={{ width: '18rem' }}
                className=""
            >
                {
                    homeGames.games ?
                        homeGames.games.map((item) => (
                            <div key={item.id}>
                                <Card.Img
                                    style={{width:'100%',height:'400px'}}
                                    src={item.image}
                                    alt="game images"
                                />
                                <Card.Body>
                                <div className="author">
                                    <Moment format="DD MMMM">
                                        {item.createdAt}
                                    </Moment>
                                </div>
                                <div className="author">
                                    <span>{item.author} -</span>  
                                    
                                </div>
                                <div className="content">
                                    <div className="title">{item.title}</div>
                                    <div className="excerpt">{item.excerpt}</div>
                                
                                    <LinkContainer 
                                        className="mt-3"
                                        to={`/about/${item.id}`}
                                    >
                                    <Button 
                                        variant="outline-dark"
                                    >
                                        View Game
                                    </Button>
                                    </LinkContainer>
                                </div>
                                </Card.Body>
                            </div>
                        ))
                    :null
                }
            </Card>
            { loading ?
                <div style={{ textAlign:'center'}}>
                    <Spinner animation="border" role="status">
                        <span className="sr-only">loading...</span>
                    </Spinner>
                </div>
            :null}
            { !homeGames.end & !loading ? 
            <Button 
                variant="outline-dark" 
                onClick={() => loadMoreGames()}
            >
                Get more games
            </Button>
            :null}
        </>
    )    
}

export default HomeGames;
