import axios from 'axios';

const URL_SERVER = "http://localhost:3004";

export const getGames = async() => {
    try {
        // http://localhost:3004/games?/_page=1&_limit=6&_order=des
        const response = await axios.get(`${URL_SERVER}/games`);
        return {
            games: response.data
        }
    } catch(error) {
        throw error;
    }
}
