import React from 'react';
import { Link } from "react-router-dom";
import { Button, Collapse, NavbarBrand, Navbar, NavItem, NavLink, Nav, Container, Row, Col, UncontrolledTooltip } from "reactstrap";

const PageNavBar = () => {
    const [collapseOpen, setCollapseOpen] = React.useState(false);
    const [collapseOut, setCollapseOut] = React.useState("");
    const [color, setColor] = React.useState("navbar-transparent");

    React.useEffect(() => {
        window.addEventListener("scroll", changeColor);
        return function cleanup() {
            window.removeEventListener("scroll", changeColor);
        };
    },[]);

    const changeColor = () => {
        if( document.documentElement.scrollTop > 99 || document.body.scrollTop > 99) {
            setColor("bg-info");
        }
        else if (document.documentElement.scrollTop < 100 || document.body.scrollTop < 100) {
            setColor("navbar-transparent");
        }
    };

    const toggleCollapse = () => {
        document.documentElement.classList.toggle("nav-open");
        setCollapseOpen(!collapseOpen);
    };
    const onCollapseExisting = () => {
        setCollapseOut("collapsing-out");
    };
    const onCollapseExited = () => {
        setCollapseOut("");
    };
    return (
        <>
            
            <Navbar className={"fixed-top " + color} color-on-scroll="100" expand="lg">
                <Container>
                    <div className="navbar-translate">
                        <NavbarBrand to="/" id="navbar-brand" tag={Link}>
                            <span>Gefeasoft</span>
                            Kids Game
                        </NavbarBrand>
                        <UncontrolledTooltip placement="bottom" target="navbar-brand">
                            Gefeasoft Creates simulation softwares, and Game development.
                        </UncontrolledTooltip>
                        <button aria- className="navbar-toggler navbar-toggler"></button>
                    </div>
                </Container>

            </Navbar>
        </>
    )
}

export default PageNavBar;
